with open('math_results.txt', 'r') as file:
    lines = file.readlines()

corrected_lines = []
for line in lines:
    if '5 + 3' in line:
        corrected_lines.append('5 + 3 = 8\n')
    elif '10 - 2' in line:
        corrected_lines.append('10 - 2 = 8\n')
    elif '4 * 2' in line:
        corrected_lines.append('4 * 2 = 8\n')
    else:
        corrected_lines.append(line)

with open('math_results.txt', 'w') as file:
    file.writelines(corrected_lines)
